import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import classNames from 'classnames';
import {
  getMovies,
  evoluateMovie,
  deleteMovie,
  setCategories,
} from '../../store/actions';
import {
  Movie,
  MovieImageBox,
  MovieCategory,
  MovieDelete,
  MovieImageFader,
  MovieImage,
  MovieImageBottom,
  MovieLikesBox,
  MovieLike,
  CheckedIcon,
  MovieCounter,
  MovieDislike,
  MovieTextBox,
  MovieTitle,
} from './style.module.scss';

const Card = (props) => {
  const dispatch = useDispatch();
  const { movie } = props;

  const makeCategories = (movies) => {
    const categories = movies.map((movie) => ({
      id: movie.id,
      title: movie.category,
      checked: false,
    }));
    const titles = categories.map((cat) => cat.title);
    const filteredCategories = categories.filter(({title}, index) => !titles.includes(title, index + 1));
    dispatch(setCategories(filteredCategories));
  }

  const onEvoluateMovie = (id, action) => {
    dispatch(evoluateMovie(id, action));
  }

  const onDeleteMovie = (id) => {
    dispatch(deleteMovie(id));
  }

  const Icon = () => (
    <svg viewBox="0 0 24 24" fill="currentColor" preserveAspectRatio="xMidYMid meet" focusable="false">
      <g>
        <path d="M1 21h4V9H1v12zm22-11c0-1.1-.9-2-2-2h-6.31l.95-4.57.03-.32c0-.41-.17-.79-.44-1.06L14.17 1 7.59 7.59C7.22 7.95 7 8.45 7 9v10c0 1.1.9 2 2 2h9c.83 0 1.54-.5 1.84-1.22l3.02-7.05c.09-.23.14-.47.14-.73v-1.91l-.01-.01L23 10z"></path>
      </g>
    </svg>
  )

  return (
    <div key={movie.id} className={Movie}>
      <div className={MovieImageBox}>
        <div className={MovieCategory}>{movie.category}</div>
        <div className={MovieDelete} onClick={() => onDeleteMovie(movie.id)}></div>
        <div className={MovieImageFader}></div>
        <img className={MovieImage} src={movie.poster} alt="poster" />
        <div className={MovieImageBottom}>
          <div className={MovieLikesBox}>
            <div
              className={classNames(MovieLike, movie.setLike ? CheckedIcon : '')}
              onClick={() => onEvoluateMovie(movie.id, 'like')}
            >
              <Icon />
            </div>
            <div className={classNames(MovieCounter, movie.setLike ? CheckedIcon : '')}>
              {movie.likes.toLocaleString()}
            </div>
          </div>
          <div className={MovieLikesBox}>
            <div
              className={classNames(MovieDislike, movie.setDislike ? CheckedIcon : '')}
              onClick={() => onEvoluateMovie(movie.id, 'dislike')}
            >
              <Icon />
            </div>
            <div className={classNames(style.MovieCounter, movie.setDislike ? CheckedIcon : '')}>
              {movie.dislikes.toLocaleString()}
            </div>
          </div>
        </div>
      </div>
      <div className={MovieTextBox}>
        <div className={MovieTitle}>{movie.title}</div>
      </div>
    </div>
  );
}

export default Card;
