import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import classNames from 'classnames';
import {
  getMovies,
  evoluateMovie,
  deleteMovie,
  toggleCategoryState,
  setCategories,
} from './store/actions';
import style from './style.module.scss';

const App = () => {
  const dispatch = useDispatch();
  const { moviesList, categories } = useSelector((store) => store.movies);
  const [filter, setFilter] = useState([]);

  useEffect(() => {
    dispatch(getMovies());
  }, []);

  useEffect(() => {
    makeCategories(moviesList);
  }, [moviesList]);

  useEffect(() => {
    const checkedCategories = categories.filter((cat) => cat.checked).map((cat) => cat.title);
    setFilter(checkedCategories);
  }, [categories]);

  useEffect(() => {
    // filter.forEach((cat) => {
    //   const filtered = moviesList.filter((movie) => movie.category === cat)
    //   console.log('filtered', filtered);
    //   return [...filtered];
    // })
    var filtered = moviesList.filter((element) => {
      // Create an array using `.split()` method
      var cats = element.category.split(' ');

      // Filter the returned array based on specified filters
      // If the length of the returned filtered array is equal to
      // length of the filters array the element should be returned  
      return cats.filter(function(cat) {
          return filtersArray.indexOf(cat) > -1;
      }).length === filtersArray.length;
   });
  }, [filter]);

  console.log(moviesList);
  console.log('filter', filter);

  const makeCategories = (movies) => {
    const categories = movies.map((movie) => ({
      id: movie.id,
      title: movie.category,
      checked: false,
    }));
    const titles = categories.map((cat) => cat.title);
    const filteredCategories = categories.filter(({title}, index) => !titles.includes(title, index + 1));
    dispatch(setCategories(filteredCategories));
  }

  // const setLike = (id) => {
  //   dispatch(likeMovie(id));
  // }

  // const setDislike = (id) => {
  //   dispatch(dislikeMovie(id));
  // }

  const onEvoluateMovie = (id, action) => {
    dispatch(evoluateMovie(id, action));
  }

  const onDeleteMovie = (id) => {
    dispatch(deleteMovie(id));
  }

  const onToggleChecked = (id) => {
    dispatch(toggleCategoryState(id));
  }

  return (
    <div className={style.Wrapper}>
      <div className={style.Content}>
        <div className={style.CategoriesFilter}>
          {categories.map((category) => (
            <div
              key={category.id}
              className={classNames(style.Category, category.checked ? style.CategorySelected : '')}
              onClick={() => onToggleChecked(category.id)}
            >
              {category.title}
            </div>
          ))}
        </div>
        <div className={style.Movies}>
          {moviesList.map((movie) => (
            <div key={movie.id} className={style.Movie}>
              <div className={style.MovieImageBox}>
                <div className={style.MovieCategory}>{movie.category}</div>
                <div className={style.MovieDelete} onClick={() => onDeleteMovie(movie.id)}></div>
                <div className={style.MovieImageFader}></div>
                <img className={style.MovieImage} src={movie.poster} alt="poster" />
                <div className={style.MovieImageBottom}>
                  <div className={style.MovieLikesBox}>
                    <div className={style.MovieLike} onClick={() => onEvoluateMovie(movie.id, 'like')}></div>
                    <div className={style.MovieLikeCounter}>{movie.setLike ? movie.likes + 1 : movie.likes - 1}</div>
                  </div>
                  <div className={style.MovieLikesBox}>
                    <div className={style.MovieDislike} onClick={() => onEvoluateMovie(movie.id, 'dislike')}></div>
                    <div className={style.MovieDislikeCounter}>{movie.dislikes}</div>
                  </div>
                </div>
              </div>
              <div className={style.MovieTextBox}>
                <div className={style.MovieTitle}>{movie.title}</div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
