import React, { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import classNames from 'classnames';

import {
  getMovies,
  toggleCategoryState,
  setCategories,
} from './store/actions';

import LikeDislikeControl from '../LikeDislikeControl';

import { deleteMovie } from '../../store/actions';

import {
  Movie,
  MovieImageBox,
  MovieCategory,
  MovieDelete,
  MovieImageFader,
  MovieImage,
  MovieImageBottom,
  MovieTextBox,
  MovieTitle,
} from './style.module.scss';

const Filter = (props) => {
  const dispatch = useDispatch();
  const { movie } = props;

  useEffect(() => {
    makeCategories(moviesList);
  }, [moviesList]);

  const makeCategories = (movies) => {
    const categories = movies.map((movie) => ({
      id: movie.id,
      title: movie.category,
      checked: false,
    }));
    const titles = categories.map((cat) => cat.title);
    const filteredCategories = categories.filter(({title}, index) => !titles.includes(title, index + 1));
    dispatch(setCategories(filteredCategories));
  }

  const onDeleteMovie = (id) => {
    dispatch(deleteMovie(id));
  }

  const onToggleChecked = (id) => {
    dispatch(toggleCategoryState(id));
  }

  return (
    <div className={style.CategoriesFilter}>
      {categories.map((category) => (
        <div
          key={category.id}
          className={classNames(style.Category, category.checked ? style.CategorySelected : '')}
          onClick={() => onToggleChecked(category.id)}
        >
          {category.title}
        </div>
      ))}
    </div>
  );
}

export default Filter;
