import { GET_FORMATS_CMS } from './const';

const initState = {
  formats: [],
  formatsAppointment: [],
  formatsBanners: [],
  dictionary: {
    formatNames: null,
    formatHumanReadable: null,
  },
};

const formatsReducer = (state = initState, action) => {
  switch (action.type) {
    case GET_FORMATS_CMS:
      return {
        ...state,
        formats: action.formats,
        formatsAppointment: action.formatsForAppointment,
        formatsBanners: action.formatsForBanners,
        dictionary: {
          ...state.dictionary,
          formatNames: action.formatNames,
          formatHumanReadable: action.formatHumanReadable,
        },
      };

    default:
      return state;
  }
};

export default formatsReducerCms;
