import { GET_MOVIES, DELETE_MOVIE } from './const';

const initState = {
  moviesList: [],
};

const movieReducer = (state = initState, action) => {
  switch (action.type) {
    case GET_MOVIES:
      return {
        ...state,
        moviesList: action.moviesList,
      };

    case DELETE_MOVIE: {
			const copyState = [...state.moviesList]
			const filtered = copyState.moviesList.filter((movie) => movie.id !== action.id);

      return {
        ...state,
        moviesList: filtered,
      };
    }

    default:
      return state;
  }
};

export default movieReducer;
