import { GET_MOVIES } from './const';

const initState = {
  moviesList: [],
};

const movieReducer = (state = initState, action) => {
  switch (action.type) {
    case GET_MOVIES:
      return {
        ...state,
        moviesList: action.moviesList,
      };
    case GET_MOVIES:
      return {
        ...state,
        moviesList: action.moviesList,
      };

    default:
      return state;
  }
};

export default movieReducer;
