import { GET_FORMATS_CMS } from './const';

const initState = {
  moviesList: [],
};

const movieReducer = (state = initState, action) => {
  switch (action.type) {
    case 'GET_MOVIES':
      return {
        ...state,
        moviesList: action.moviesList,
        formatsAppointment: action.formatsForAppointment,
        formatsBanners: action.formatsForBanners,
        dictionary: {
          ...state.dictionary,
          formatNames: action.formatNames,
          formatHumanReadable: action.formatHumanReadable,
        },
      };

    default:
      return state;
  }
};

export default movieReducer;
