// import { createSlice } from '@reduxjs/toolkit';
import { movies$ } from './api';
import {
  GET_MOVIES,
  DELETE_MOVIE,
  DISLIKE_MOVIE,
  LIKE_MOVIE,
  SET_FILTERS_CATEGORIES,
} from './const';

export const getMovies = () => (dispatch) => {
  movies$.then((res) => {
    dispatch({ type: GET_MOVIES, moviesList: res });
    dispatch({ type: SET_FILTERS_CATEGORIES, categories: res });
  });
};

export const deleteMovie = (id) => (dispatch) => {
  dispatch({ type: DELETE_MOVIE, id });
};

export const dislikeMovie = (id) => (dispatch) => {
  dispatch({ type: DISLIKE_MOVIE, id });
};

export const likeMovie = (id) => (dispatch) => {
  dispatch({ type: LIKE_MOVIE, id });
};

