import {
  GET_MOVIES,
  DELETE_MOVIE,
  DISLIKE_MOVIE,
  LIKE_MOVIE,
  SET_FILTERS_CATEGORIES,
} from './const';

const initState = {
  moviesList: [],
	categories: [],
};

const movieReducer = (state = initState, action) => {
  switch (action.type) {
    case GET_MOVIES:
      return {
        ...state,
        moviesList: action.moviesList,
      };

    case SET_FILTERS_CATEGORIES:
      return {
        ...state,
        categories: action.moviesList,
      };

    case DELETE_MOVIE: {
			const copyState = [...state.moviesList]
			const filtered = copyState.filter((movie) => movie.id !== action.id);

      return {
        ...state,
        moviesList: filtered,
      };
    }

    case DISLIKE_MOVIE: {
			const copyState = [...state.moviesList]
			const dislikedMovie = copyState.find((movie) => movie.id === action.id);
			dislikedMovie.dislikes -= 1

      return {
        ...state,
        moviesList: copyState,
      };
    }

    case LIKE_MOVIE: {
			const copyState = [...state.moviesList]
			const likedMovie = copyState.find((movie) => movie.id === action.id);
			likedMovie.likes += 1

      return {
        ...state,
        moviesList: copyState,
      };
    }

    default:
      return state;
  }
};

export default movieReducer;
