import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
// import logo from './logo.svg';
// import { Counter } from './features/counter/Counter';
import { getMovies, moviesStore } from './store/actions';
import style from './style.scss';
import './App.css';

const App = () => {
  const dispatch = useDispatch();
  const movies = useSelector(moviesStore);

  useEffect(() => {
    dispatch(getMovies());
  });

  console.log('movies', movies);

  const setLike = () => {
    console.log('like');
  }

  const setDislike = () => {
    console.log('dislike');
  }

  return (
    <div className="Wrapper">
      <div className="Content">
        <div className={style.Movies}>
          {movies.moviesList.map((movie) => (
            <div key={movie.id} className="Movie">
              <div className="MovieImageBox" style={{ backgroundImage: `url(${movie.poster})` }}>
                <div className="MovieImageBottom">
                  <div className="MovieLikesBox">
                    <div className="MovieLike" onClick={setLike}></div>
                    <div className="MovieLikeCounter">{movie.likes}</div>
                  </div>
                  <div className="MovieLikesBox">
                    <div className="MovieDislike" onClick={setDislike}></div>
                    <div className="MovieLikeCounter">{movie.dislikes}</div>
                  </div>
                </div>
              </div>
            </div>
          ))}
          {/* <Counter /> */}
        </div>
      </div>
    </div>
  );
}

export default App;
